import React,{useRef,useEffect} from "react";
import {Link} from 'react-router-dom';
import {useFormik} from 'formik';
import * as yup from 'yup';
import YupPassword from 'yup-password';
import { useDispatch,useSelector } from 'react-redux';
import {loginUser} from '../../store/authenticationSlice'

import 'Login.css'

YupPassword(yup);


export default function Login(){

    var emailRef = useRef();
    var passwordRef = useRef();

    //Yup Validations
    const credentialsValidation = yup.object().shape({
        email:yup.string()
        .required('Email Required')
        .email('Invalid Email'),

        password:yup.string()
        .required('Password Required')
        .min(5,'Minimum Lenght:5')
        .max(15,'Maximum Lenght:15')
    })


    //Dispatch
    const dispatch = useDispatch();

    var formik = useFormik({
        initialValues:{
            email:'',
            password:''
        },
        
        validationSchema:credentialsValidation,

        onSubmit:(values)=>{
            dispatch(loginUser(values))
        }
    })

    // function loginuser(values){
    //     console.log("Hey Bruh!!!",values);
    // }

    


    return (
        <div className="loginGlobalDiv">
        <div>
            <h1>Login Page</h1>
            <form onSubmit={formik.handleSubmit}>
                <input type="text" placeholder="Enter email" name="email" {...formik.getFieldProps('email')}  required ref={emailRef}/>
                {formik.touched.email && formik.errors.email ? <div>{formik.errors.email}</div>:null}
                <br/>
                <input type="password" placeholder="Password" name="password" {...formik.getFieldProps('password')}  required ref={passwordRef}/>
                {formik.touched.password && formik.errors.password ? <div>{formik.errors.password}</div>:null}

                <br/>
                <button type="submit">Login</button>

                <div>
                    New User, <Link to='/register'>Register</Link> Here
                </div>
            </form>
        </div>
        </div>
    )
}
