import React,{useRef,useEffect}from 'react'
import { Link } from 'react-router-dom';
import {useFormik} from 'formik'
import * as yup from 'yup'
import YupPassword from 'yup-password'

YupPassword(yup);


export default function Register(){

    const emails = [];

    var usernameRef = useRef();
    var emailRef = useRef();
    var phonenumberRef = useRef();
    var passwordRef = useRef();
    var cpasswordRef = useRef();

    const credentialsValidation = yup.object().shape({
        username:yup.string()
        .required('Username Required')
        .min(5,'Minimum Length 5')
        .max(12,'Maximum Length 12'),

        email:yup.string()
        .required('Email Required')
        .email('Invalid Email')
        .notOneOf(emails,'Email Already Registered'),

        password:yup.string()
        .required('Password Required')
        .min(5,'Minimum 5 atleast 1 capital')
        .max(15,'Maximum 15')
        .minUppercase(1,'1 Capital Letter'),

        confirmpassword:yup.string()
        .required(' Confirm Password Required')
        .oneOf([yup.ref('password'),null],'password and confirm passwords is not same'),

        phonenumber:yup.string()
        .required('phone number rrequired')
        .min(10,'Not a Valid Phone number')
        .max(10,'Not a Valid Phone number')
        .matches(/^((\\+[1-9]{1,4}[ \\-]*)|(\\([0-9]{2,3}\\)[ \\-]*)|([0-9]{2,4})[ \\-]*)*?[0-9]{3,4}?[ \\-]*[0-9]{3,4}?$/,'Not a valid phonne number'),

    })

    const formik = useFormik({
        initialValues:{
            username:'',
            email:'',
            phonenumber:'',
            password:'',
            confirmpassword:''
        },
        validationSchema:credentialsValidation,
        onSubmit:registerUser
    })

    function registerUser(values){
        console.log("Hey Register",values);
    }

    return(
        <div>
            <h1>Register Page</h1>
            <form onSubmit={formik.handleSubmit}>
            <input type="text"  placeholder="Username" {...formik.getFieldProps('username')} required ref={uref}/>  
                                
            {formik.touched.username&&formik.errors.username?<div>{formik.errors.username}</div>:null}
                
            <input type="email"  placeholder="Email Id" className="form-control form-control-sm mt-3" autoComplete='new-password'  {...formik.getFieldProps('email')} required ref={eref}/>  
            {formik.touched.email&&formik.errors.email?<div>{formik.errors.email}</div>:null}
            
                
            <input type="number"   placeholder="phone Numer" className="form-control form-control-sm mt-3" autoComplete='new-password'  {...formik.getFieldProps('phonenumber')} required ref={pnref}/>  
            {formik.touched.phonenumber&&formik.errors.phonenumber?<div>{formik.errors.phonenumber}</div>:null}
            <input type="password"    placeholder="Password"  className="form-control form-control-sm mt-3"  {...formik.getFieldProps('password')} ref={pref}/>  
            
            {formik.touched.password&&formik.errors.password?<div>{formik.errors.password}</div>:null}
            <input type="password"   placeholder="Confirm  Password" className="form-control form-control-sm mt-3"  {...formik.getFieldProps('confirmpassword')} required ref={cpref} />  
            {formik.touched.confirmpassword&&formik.errors.confirmpassword?<div>{formik.errors.confirmpassword}</div>:null}
                <button>Register</button>
            </form>
        </div>
    )
}
