import {createSlice} from '@reduxjs/toolkit';

export const adminSlice = createSlice({
    name:'admin',
    initialState:{
        totalDrivers:[]
    },
    reducers:{
        increment:(state)=>{
            state.count = state.count +1;
        },
        decrement:(state)=>{
            state.count = state.count -1
        },
        reset:(state)=>{
            state.count = 0;
        }
    }
});

export const {increment,decrement,reset}  = adminSlice.actions;
export default adminSlice.reducer;
