import {createSlice} from '@reduxjs/toolkit';

export const adminSlice = createSlice({
    name:'admin',
    initialState:{
        totalDrivers:[]
    },
    reducers:{
        loadDrivers:(state,action)=>{
            state.totalDrivers=[];
            state.totalDrivers.push(action.payload);
          }
    }
});

export function getDriverData(){
    return ((dispatch)=>{
      fetch('http://localhost:2424/getDrivers')
      .then((res)=>res.json())
      .then((data)=>{
        dispatch(loadDrivers(data))
      }).catch((err)=>console.log("Err::",err))
    })
  }

  export function addCategory(newCategory){
      return((dispatch)=>{
        fetch("http://localhost:2424/addCategory",{
            method:'POST',
            headers:{
                'Content-Type':'application/json'
            },
            body:JSON.stringify(newCategory)
        })
        .then((res)=>{
            if(res.status===200){
                alert("Category Added Successfully")
                window.location.reload();
            }

            if(res.status===400){
                alert("Category Already Exist")
                window.location.reload();
            }
            if(res.status===500){
                alert("Internal Server Error")
                window.location.reload();
            }
        })
      })
  }

export const {loadDrivers}  = adminSlice.actions;
export default adminSlice.reducer;
