import React,{useRef} from "react"
import {Link} from 'react-router-dom'
import {useFormik} from 'formik'
import * as yup from 'yup'
import YupPassword from 'yup-password'
// import { useDispatch,useSelector } from 'react-redux';

YupPassword(yup);


export default function Login(){


    var emailRef = useRef();
    var passwordRef = useRef();

    //Yup Validations

    const validations = yup.object.shape({
        email:yup.string().required('Email Required').email('Invalid Email'),
        password:yup.string().required('Password Required').min(5,'Minimum Lenght:5')
    })


    // const dispatch = useDispatch();

    var loginform = useFormik({
        initialValues:{
            email:'',
            password:''
        },
        
        validationSchema:validations,

        onSubmit:loginuser
    })

    function loginuser(values){
        console.log('Hey Bruh!!',values);
    }


    return (
        <div>
            <h1>Login Page</h1>
            <form onSubmit={loginform.handleSubmit}>
                <input type="email" placeholder="Enter email" name="email" {...loginform.getFieldProps('email')} ref={emailRef} required/>
                {loginform.touched.email && loginform.errors.email ? <div>{loginform.errors.email}</div>:null}
                <br/>
                <input type="password" placeholder="Password" name="password" {...loginform.getFieldProps('password')} ref={passwordRef} required/>
                {loginform.touched.password && loginform.errors.password ? <div>{loginform.errors.password}</div>:null}

                <br/>
                <button>Login</button>

                <div>
                    New User, <Link to='/register'>Register</Link> Here
                </div>
            </form>
        </div>
    )
}
