import React,{useRef,useEffect}from 'react'
import { Link } from 'react-router-dom';
import {useFormik} from 'formik'
import * as yup from 'yup'
import YupPassword from 'yup-password'

YupPassword(yup);


export default function Register(){

    const emails = [];

    var usernameRef = useRef();
    var emailRef = useRef();
    var phonenumberRef = useRef();
    var passwordRef = useRef();
    var cpasswordRef = useRef();

    const credentialsValidation = yup.object().shape({
        username:yup.string()
        .required('Username Required')
        .min(5,'Minimum Length 5')
        .max(12,'Maximum Length 12'),

        email:yup.string()
        .required('Email Required')
        .email('Invalid Email')
        .notOneOf(emails,'Email Already Registered'),

    })

    return(
        <div>
            <h1>Register Page</h1>
            <form action="#">
                <input type="text" placeholder="Full Name" name="name" required/>
                <br/>
                <input type="text" placeholder="Enter Mail" name="email" required />
                <br/>
                <input type="number" placeholder="Phone Number" name="phonenumber" required />
                <br/>
                <input type="password" placeholder="Password" name="password" required />
                <br/>
                <input type="password" placeholder="Confirm Password" name="cpassword" required />
                <br/>
                <button>Register</button>
            </form>
        </div>
    )
}
