import { createSlice } from '@reduxjs/toolkit';
export const driverSlice= createSlice({
  name: 'driver',
  initialState: {
    totalDrivers:[]
  },
  reducers: {
    loadDrivers:(state,action)=>{
      state.totalDrivers=[];
      state.totalDrivers.push(action.payload);
      console.log("State;;",state.totalDrivers)
    }
  },
});

export function getDriverData(){
  return ((dispatch)=>{
    fetch('http://localhost:2424/getDrivers')
    .then((res)=>res.json())
    .then((data)=>{
      console.log("getDriverData::",data)
      dispatch(loadDrivers(data))
    }).catch((err)=>console.log("Err::",err))
  })
}

export const { loadDrivers} = driverSlice.actions;
export default driverSlice.reducer;
